def downloadHlData(csv_url):
    response = request.urlopen(csv_url)
    csv = response.read()
    csv_string = str(csv)
    lines = csv_string.split("\\n")
    destination_url = "downloadFile1.csv"
    fileOpen = open(destination_url, "w")
    for line in lines:
        fileOpen.write(line + "\n")

    fileOpen.close()

